      from: `${req.body.email}`,
      to: 'paquetraphaelle@gmail.com',
      subject: req.body.subject,
      text: `
      from:
      ${req.body.name} 

      contact: 
      ${req.body.email}

      message: 

      ${req.body.text}`
    }

// error handling goes here. 

