    password: req.body.password
  });
  if(signinUser) {
    res.send({
      _id: signinUser.id,
      name: signinUser.name,
      email: signinUser.email,
      isAdmin: signinUser.isAdmin,
      token: getToken(signinUser)
    })
  } else {
    res.status(404).send({msg: 'Invalid Email or password.'})
  }
})

